import { useState } from "react"
import styles from './Condicional.module.css'
function Condicional(){

    const[email, setEmail] = useState()
    const[userEmail, setUserEmail] = useState()

    function enviarEmail(e){
        e.preventDefault()
        setUserEmail(email)
        console.log(userEmail)
    }
    function limparEmail(){
        setUserEmail('')
    }

    return(
        <div>
            <h2>Cadastre o seu email</h2>
            <form>
                <input type="email" placeholder="Digite seu email" onChange={(e) => setEmail(e.target.value)}/>
                <button type="submit" onClick={enviarEmail} className={styles.buttonForm}>Enviar</button>
                {userEmail &&(
                    <div>
                       <p>O email do usuario e: {userEmail}</p> 
                       <button className={styles.button2Form} onClick={{limparEmail}}>Limpar Email</button>
                    </div>
                )}
           </form>

        </div>
    )
}
export default Condicional
